mapper.select:
		/* findAll */
		SELECT	A.*, B.*
		  FROM	ROOM A
	 LEFT JOIN	ROOMCATEGORY B
	 		ON	A.room_no = B.room_no
	 
	 	
			AND	A.id = #{id}
	 	
	 	
			AND	A.room_type = #{roomType}
	 	
	 	
			AND	B.transaction_type = #{transactionType}
	 	
	 	
			AND	B.deposit_price >= #{min} AND B.deposit_price  <=  #{max}
	 	
	 	
			AND	B.monthly_price >= #{min} AND B.monthly_price  <=  #{max}

		/* findDetail */
		SELECT	A.*, B.*
		  FROM	ROOM A
	 LEFT JOIN	ROOMCATEGORY B
	 		ON	A.room_no = B.room_no
	 	 WHERE	A.room_no = #{roomNo}
